import React, { useState, useEffect } from 'react';
import './carousel.css'; // Import your CSS file for styling

const slides = [
  {
    id: 1,
    imageUrl: 'https://cdn.pixabay.com/photo/2015/04/23/22/00/tree-736885_1280.jpg',
    altText: 'First slide',
    caption: 'Beautiful Tree',
  },
  {
    id: 2,
    imageUrl: 'https://cdn.pixabay.com/photo/2015/04/23/22/00/tree-736885_1280.jpg',
    altText: 'Second slide',
    caption: 'Scenic Landscape',
  },
  {
    id: 3,
    imageUrl: 'https://cdn.pixabay.com/photo/2015/04/23/22/00/tree-736885_1280.jpg',
    altText: 'Third slide',
    caption: 'Nature Beauty',
  },
];

const Carousel = () => {
    const [currentSlide, setCurrentSlide] = useState(0);
    let interval;
  
    useEffect(() => {
      interval = setInterval(() => {
        nextSlide();
      }, 5000); // Change slide every 5 seconds
  
      return () => {
        clearInterval(interval);
      };
    }, []);
  
    const nextSlide = () => {
      setCurrentSlide((currentSlide + 1) % slides.length);
    };
  
    const prevSlide = () => {
      setCurrentSlide(currentSlide === 0 ? slides.length - 1 : currentSlide - 1);
    };
  
    return (
      <div className="carousel-container">
        <div
          id="carouselExampleIndicators"
          className="carousel slide"
          data-ride="carousel"
          onMouseEnter={() => clearInterval(interval)}
          onMouseLeave={() => {
            clearInterval(interval);
            interval = setInterval(() => {
              nextSlide();
            }, 5000);
          }}
        >
          <ol className="carousel-indicators">
            {slides.map((slide, index) => (
              <li
                key={slide.id}
                data-target="#carouselExampleIndicators"
                data-slide-to={index}
                className={index === currentSlide ? 'active' : ''}
              ></li>
            ))}
          </ol>
          <div className="carousel-inner">
            {slides.map((slide, index) => (
              <div key={slide.id} className={`carousel-item ${index === currentSlide ? 'active' : ''}`}>
                <img className="d-block w-100" src={slide.imageUrl} alt={slide.altText} />
                <div className="carousel-caption">
                  <h3>{slide.caption}</h3>
                </div>
              </div>
            ))}
          </div>
          <a className="carousel-control-prev" href="#carouselExampleIndicators" role="button" data-slide="prev" onClick={prevSlide}>
            <span className="carousel-control-prev-icon" aria-hidden="true"></span>
            <span className="sr-only">Previous</span>
          </a>
          <a className="carousel-control-next" href="#carouselExampleIndicators" role="button" data-slide="next" onClick={nextSlide}>
            <span className="carousel-control-next-icon" aria-hidden="true"></span>
            <span className="sr-only">Next</span>
          </a>
        </div>
      </div>
    );
  };
  
  export default Carousel;
